mapper.select:
SELECT 
    id, agent_id, client_id, sequence, create_time
FROM 
    ai_agent_client


        AND id = #{id}


        AND agent_id = #{agentId}


        AND client_id = #{clientId}


        AND sequence = #{sequence}


ORDER BY agent_id, sequence, create_time DESC

    LIMIT #{offset}, #{limit}
