mapper.insert:
INSERT INTO ai_agent_flow_config (
    agent_id, client_id, client_name, client_type, sequence, step_prompt, create_time
) VALUES (
    #{agentId}, #{clientId}, #{clientName}, #{clientType}, #{sequence}, #{stepPrompt}, #{createTime}
)

mapper.update:
UPDATE ai_agent_flow_config SET
    agent_id = #{agentId},
    client_id = #{clientId},
    sequence = #{sequence},
    step_prompt = #{stepPrompt}
WHERE id = #{id}

mapper.select:
SELECT id, agent_id, client_id, client_name, client_type, sequence, step_prompt, create_time
FROM ai_agent_flow_config
WHERE id = #{id}

SELECT id, agent_id, client_id, client_name, client_type, sequence, step_prompt, create_time
FROM ai_agent_flow_config
WHERE agent_id = #{agentId} AND client_id = #{clientId}
