mapper.insert:
INSERT INTO ai_agent (
    agent_name, channel, description, status, create_time, update_time
) VALUES (
    #{agentName}, #{description}, #{status}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT 
    id, agent_name, channel, description, status, create_time, update_time
FROM 
    ai_agent


        AND id = #{id}


        AND agent_name LIKE CONCAT('%', #{agentName}, '%')


        AND status = #{status}


ORDER BY create_time DESC

    LIMIT #{offset}, #{limit}
