mapper.insert:
INSERT INTO ai_chat_session (
    id, agent_id, rag_id, session_title, message_count, last_message_time, create_time, update_time
) VALUES (
    #{id}, #{agentId}, #{ragId}, #{sessionTitle}, #{messageCount}, #{lastMessageTime}, NOW(), NOW()
)

mapper.select:
SELECT id, agent_id, rag_id, session_title, message_count, last_message_time, create_time, update_time
FROM ai_chat_session
WHERE id = #{id}

SELECT id, agent_id, rag_id, session_title, message_count, last_message_time, create_time, update_time FROM ai_chat_session WHERE agent_id = #{agentId}
AND (rag_id = #{ragId} OR rag_id IS NULL)
ORDER BY last_message_time DESC, create_time DESC LIMIT 50
