mapper.select:
SELECT id, client_id, system_prompt_id, create_time
FROM ai_client_system_prompt_config
WHERE id = #{id}

SELECT id, client_id, system_prompt_id, create_time
FROM ai_client_system_prompt_config
WHERE client_id = #{clientId}

SELECT 
    id, client_id, system_prompt_id, create_time
FROM 
    ai_client_system_prompt_config


        AND id = #{id}


        AND client_id = #{clientId}


        AND system_prompt_id = #{systemPromptId}


ORDER BY create_time DESC

    LIMIT #{offset}, #{limit}

mapper.insert:
